import { UPDATE_CALCULATION_AND_RESULT, CLEAR_ALL,EQUAL_TO } from '../constants';

const initialState = {
  calculation: [],
  result: 0
};

const calculatorReducer = (state = initialState, action) => {
  switch (action.type) {
    case UPDATE_CALCULATION_AND_RESULT:
      return {
        calculation: action.payload.calculation,
        result: action.payload.result
      };
    case CLEAR_ALL:
      return {
      	calculation: [],
        result: 0
      };
    case EQUAL_TO:
      return {
        calculation: [],
        result: action.payload.result
      };  
    default:
      return state;
  }
};

export default calculatorReducer;
